class Ball {
  float x;
  float y;
  float speedX;
  float speedY;
  float diameter;
  int c;
  
  // Constructor method
  Ball(float tempX, float tempY, float tempDiameter) {
    x = tempX;
    y = tempY;
    diameter = tempDiameter;
    speedX = 0;
    speedY = 0;
    c = (225); 
  }
  
  public void move() {
    // Add speed to location
    y = y + speedY;
    x = x + speedX;
  }
  
  public void display() {
    fill(c); //set the drawing color
    ellipse(x,y,diameter,diameter); //draw a circle
  }
  
  //functions to help with collision detection
  public float left(){
    return x-diameter/2;
  }
  public float right(){
    return x+diameter/2;
  }
  public float top(){
    return y-diameter/2;
  }
  public float bottom(){
    return y+diameter/2;
  }

}

